mapper.select:
select t2.store_id storeId, t2.store_name storeName, t2.phone phone, t2.address address, t1.user_name userName, t2.invite_code inviteCode, t1.user_acct userAcct, t2.version version from t_sys_user t1, t_info_store t2 where t1.user_id = t2.user_id and t1.user_id = #{userId} and t1.is_delete = 0 and t2.is_delete = 0
and t2.store_id like concat('%', #{storeId}, '%')
and t2.store_name like concat('%', #{storeName}, '%')
and t1.user_name like concat('%', #{userName}, '%')
and t2.province = #{provinceId}
and t2.city = #{cityId}
and t2.area = #{areaId}
order by t2.create_time desc

select t2.store_id storeId, t2.store_name storeName, t2.phone phone, t2.address address, t1.user_name userName, t2.invite_code inviteCode, t1.user_acct userAcct, t2.version version from t_sys_user t1, t_info_store t2 where t1.user_id = t2.user_id and t1.is_delete = 0 and t2.is_delete = 0
and t2.store_id like concat('%', #{storeId}, '%')
and t2.store_name like concat('%', #{storeName}, '%')
and t1.user_name like concat('%', #{userName}, '%')
and t2.province = #{provinceId}
and t2.city = #{cityId}
and t2.area = #{areaId}
order by t2.create_time desc
